    <img class="avatar-img"  src="{{userInfo.picture}}" alt="Image Description">
  </div>
</a>

<div
  class="dropdown-menu dropdown-menu-end navbar-dropdown-menu navbar-dropdown-menu-borderless navbar-dropdown-account"
